import React, { useEffect, useState } from "react";
import "./index.css";

const images = [
  "/images/cover1.jpg",
  "/images/cover2.jpg",
  "/images/cover3.jpeg",
];

const Carousel = () => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    console.log(current);
    const id = setTimeout(() => {
      setCurrent((current + 1) % images.length);
    }, 2100);
    return () => {
      clearInterval(id);
    };
  }, [current]);

  const imageList = images.map((image) => {
    return (
      <div key={image}>
        <img
          className="ui fluid image kenburns-top layer"
          src={image}
          key={image}
        ></img>
      </div>
    );
  });
  // return
  return (
    <div class="ui two column grid">
      <div class="column">
        <div class="ui fluid image">
          <a class="ui left corner label">
            <i class="heart icon"></i> blblblblblblbl
          </a>
          <img src={imageList[0]} />
        </div>
        <div class="column">
          <div class="ui fluid image">
            <a class="ui red right corner label">
              <i class="save icon"></i>
            </a>
            <img src={imageList[0]} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Carousel;
