import React, { useEffect, useState } from "react";
import "./index.css";

const images = [
  "/images/cover1.jpg",
  "/images/cover2.jpg",
  "/images/cover3.jpeg",
];

const Carousel = () => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    console.log(current);
    const id = setTimeout(() => {
      setCurrent((current + 1) % images.length);
    }, 2100);
    return () => {
      clearInterval(id);
    };
  }, [current]);

  const imageList = images.map((image) => {
    return (
      <>
        <div class="ui top right attached label pos">
          Photo by{" "}
          <a href="https://unsplash.com/@danielmonteirox?utm_source=unsplash&utm_medium=referral&utm_content=creditCopyText">
            Daniel Monteiro
          </a>{" "}
          on{" "}
          <a href="/s/photos/belo-horizonte-transito?utm_source=unsplash&utm_medium=referral&utm_content=creditCopyText">
            Unsplash
          </a>
        </div>
        <div key={image}>
          <img
            className="ui fluid image kenburns-top layer"
            src={image}
            key={image}
          ></img>
        </div>
      </>
    );
  });
  return <div>{imageList[current]}</div>;
};

export default Carousel;
