import React, { useEffect, useState } from "react";
import "./index.css";

const images = [
  "/images/cover1.jpg",
  "/images/cover2.jpg",
  "/images/cover3.jpeg",
];

const Carousel = () => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    setCurrent(current + 1);
    const next = current % imageList.lenght;
    const id = setTimeout(() => {
      setCurrent(next);
    }, 500);
    return () => {
      clearTimeout(id);
    };
  }, []);

  const imageList = images.map((image) => {
    return <img className="ui fluid image" src={image}></img>;
  });
  return (
    <div>
      <div>{imageList[current]}</div>
    </div>
  );
};

export default Carousel;
